SELECT feeds.id,feeds.title,feeds.pic,feeds.video,feeds.likes,feeds.comments,feeds.cover_select,feeds.favours,username,portrait,`user`.id as user_id
FROM feeds,`user`
WHERE auther_id = #{id}
AND auther_id = `user`.id
ORDER BY feeds.set_time DESC
LIMIT #{start},#{size}

