SELECT dictionary.*,reciting_words.`level` FROM dictionary,reciting_words
WHERE user_id = #{id}
AND plan = #{plan}
AND dictionary.id = word_id
AND `level` = 2
AND set_time < #{two_day}
UNION
SELECT dictionary.*,reciting_words.`level` FROM dictionary,reciting_words
WHERE user_id = #{id}
AND plan = #{plan}
AND dictionary.id = word_id
AND `level` = 3
AND set_time < #{two_week}
UNION
SELECT dictionary.*,reciting_words.`level` FROM dictionary,reciting_words
WHERE user_id = #{id}
AND plan = #{plan}
AND dictionary.id = word_id
AND `level` = 4
AND set_time < #{last_month}

