mapper.select:
select 

from user
where id = #{id,jdbcType=INTEGER}

mapper.insert:
insert into user (id, username, password, 
  phone, gender, wechat, 
  qq, register_time, last_login
  )
values (#{id,jdbcType=INTEGER}, #{username,jdbcType=VARCHAR}, #{password,jdbcType=VARCHAR}, 
  #{phone,jdbcType=VARCHAR}, #{gender,jdbcType=INTEGER}, #{wechat,jdbcType=VARCHAR}, 
  #{qq,jdbcType=VARCHAR}, #{registerTime,jdbcType=VARCHAR}, #{lastLogin,jdbcType=VARCHAR}
  )

mapper.select:
SELECT

FROM user
WHERE phone = #{username}
AND password = #{password}

mapper.update:
UPDATE user
set password = #{password}
WHERE phone = #{phone}

mapper.select:
SELECT dictionary_type
FROM plans
WHERE plan = #{plan}

SELECT  word_number
FROM plans
WHERE plan = #{plan}

SELECT  portrait,gender,username,personality_signature,insist_day,my_plan
FROM `user`
WHERE id = #{id}

SELECT my_plan
   FROM user
WHERE id = #{id}

SELECT sum(learned_word_number)
   FROM take_plans
WHERE user_id = #{id}

SELECT (plans.word_number-take_plans.learned_word_number) AS result
FROM take_plans,plans
WHERE take_plans.user_id = #{id}
AND take_plans.plan = #{plan}
AND plans.id = take_plans.plan_id

SELECT portrait
   FROM user
WHERE id = #{id}

mapper.delete:
delete from take_plans
WHERE user_id = #{id}
AND plan = #{plan}

mapper.select:
SELECT my_plan,plan_days,plan_words_number,insist_day
FROM user
WHERE id = #{id}

SELECT my_plan,plan_days,plan_words_number,insist_day
FROM user
WHERE id = #{id}

mapper.update:
UPDATE user
set plan_days = #{days},
plan_words_number = #{daily_word_number}
WHERE id = #{id}

UPDATE `user`
set gender = #{gender},
personality_signature = #{personality_signature},
username = #{username}
WHERE id = #{id}

UPDATE user
set my_plan = #{plan},
plan_days = #{days},
plan_words_number = #{daily_word_number}
WHERE id = #{id}

mapper.select:
SELECT IFNULL(word_number,0)
FROM plans
WHERE plan = #{plan}

SELECT id,days,daily_word_number
FROM take_plans
WHERE plan = #{plan}
AND user_id = #{id}
